import React, { useState } from 'react';
import { Button, Typography } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import axios from 'axios';
import { API_URL } from '../utils';
import { TextareaAutosize } from '@mui/base'; // Import TextareaAutosize from @mui/base
import { styled, ThemeProvider, createTheme } from '@mui/material/styles';

// Define your color themes
const blue = {
  100: '#DAECFF',
  200: '#b6daff',
  400: '#3399FF',
  500: '#007FFF',
  600: '#0072E5',
  900: '#003A75',
};

const grey = {
  50: '#F3F6F9',
  100: '#E5EAF2',
  200: '#DAE2ED',
  300: '#C7D0DD',
  400: '#B0B8C4',
  500: '#9DA8B7',
  600: '#6B7A90',
  700: '#434D5B',
  800: '#303740',
  900: '#1C2025',
};

// Define your styled Textarea component
const StyledTextarea = styled(TextareaAutosize)(
  ({ theme }) => `
    box-sizing: border-box;
    width: 320px;
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 8px 12px;
    border-radius: 8px;
    color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
    background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
    border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
    box-shadow: 0px 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};

    &:hover {
      border-color: ${blue[400]};
    }

    &:focus {
      border-color: ${blue[400]};
      box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[600] : blue[200]};
    }

    // firefox
    &:focus-visible {
      outline: 0;
    }
  `
);

// Define your theme
const theme = createTheme({
  palette: {
    mode: 'dark', // Change to 'light' if you want to test the light mode
  },
});

// Define your functional component
export const AddTask = ({ fetchTask }) => {
  const [note, setNewNote] = useState("");

  // Function to handle adding a note
  const addNote = async () => {
    try {
      await axios.post(API_URL, {
        note: note
      });
      await fetchTask();
      setNewNote("");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <div>
        <Typography align='center' variant='h2' paddingTop={2}>My Notes List</Typography>
        <div className='addTaskForm'>
          <StyledTextarea
            maxRows={4}
            aria-label="maximum height"
            placeholder="Maximum 4 rows"
            value={note}
            onChange={(e) => setNewNote(e.target.value)}
          />
          <Button
            disabled={note.length === 0}
            variant="outlined"
            onClick={addNote}
          >
            <AddIcon />
          </Button>
        </div>
      </div>
    </ThemeProvider>
  );
};
